namespace TwelveDesktop {

    internal static class Program {
        [STAThread]
        internal static void Main() {

            ConfigLoader.LoadEngineConfig(new TwelveConfigSet() {
                CPUTextures = new string[] { "test/tileset" }
            });
            using var game = new GameManager();
            game.OnLoad += Game_OnLoad;
            game.Run(GameRunBehavior.Synchronous);
        }

        private sealed class Player:PlatformerPlayer {
            public const int EntityType = 1;

            protected override int GetEntityType() => EntityType;
            protected override float GetHorizontalForce() => Owner.Input.GetDelta2D().X;

            private Texture2D texture;

            public Player() {
                OnUpdate += Player_OnUpdate;
                OnLoad += Player_OnLoad;
                OnUnload += Player_OnUnload;
                OnRender += Player_OnRender;
            }

            private void Player_OnRender(GameTime gameTime) {
                if(OnScreen()) Draw(texture,new Rectangle(0,0,16,16));
            }


            private void Input_OnAcceptUp() {
                JumpEnd();
            }

            private void Player_OnLoad() {
                Owner.Input.OnAcceptDown += Input_OnAcceptDown;
                Owner.Input.OnAcceptUp += Input_OnAcceptUp;
                texture = Owner.Game.Content.Load<Texture2D>("player");
            }

            private void Player_OnUnload() {
                Owner.Input.OnAcceptDown -= Input_OnAcceptDown;
                Owner.Input.OnAcceptUp -= Input_OnAcceptUp;
            }

            private void Input_OnAcceptDown() => JumpStart(allowBuffer: true);

            private void Player_OnUpdate(GameTime gameTime) {
                UpdateMovement(gameTime);
                Owner.Camera.Position = Position;
            }
        }

        private sealed class BackgroundRenderer {
            private readonly string image;
            public BackgroundRenderer(string image) => this.image = image;

            private Texture2D texture;

            private GameManager game;
            public void Load(GameManager game) {
                this.game = game;
                texture = game.Content.Load<Texture2D>(image);
            }

            private Rectangle GetSource(Vector2 cameraPosition) {
                return new Rectangle((int)(cameraPosition.X * 16),0,1024,1024);
            }

            public Color Color { get; set; } = Color.White;

            public void Render(Vector2 cameraPosition) {
                game.SpriteBatch.Begin(SpriteSortMode.Deferred,null,SamplerState.LinearWrap);
                game.SpriteBatch.Draw(texture,game.Viewport.Bounds,GetSource(cameraPosition),Color);
                game.SpriteBatch.End();
            }
        }

